package com.txyz.common.cache.redis;

/**
 * 缓存时间静态常量设置
 */

public class TimeRedisKey {
    //缓存一小时
    public static  final Long HOURS=3600000l;
    //缓存半天
    public static  final Long HALFDAYS=43200000l;
    //缓存一天
    public static  final Long DAYS=86400000l;
    //缓存一周
    public static  final Long WEEKS=604800000l;
    //缓存一月
    public static  final Long MONTHS=2592000000l;
}
